import React, { useState, useEffect } from 'react';
import './styles/TopContainer.css';
import BackgroundImage1 from './styles/Images/ACShadows.jpg';
import BackgroundImage2 from './styles/Images/Phych.jpg';
import BackgroundImage3 from './styles/Images/image3.jpg';

//3 rotating images, title and captions
const figandCaption = [

  { image: BackgroundImage1, title: 'Title 1', caption: 'Caption 1' },
  { image: BackgroundImage2, title: 'Title 2', caption: 'Caption 2' },
  { image: BackgroundImage3, title: 'Title 3', caption: 'Caption 3' },

];

const TopContainer = () => {
//Sets state
  const [index, setIndex] = useState(0);

  //Creates the Intervals between swapping beteen sets
  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex + 1) % figandCaption.length);
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const { image, title, caption } = figandCaption[index];

  return (
    <section className="topContainer">
      <img src={image} alt="Background" className="backGroundImage" />
      <figcaption className="figCaption">
        <h1>{title}</h1>
        <p>{caption}</p>
        <Link src = '/about'>
        <button className="learnMoreButton">Learn More</button>
        </Link>
      </figcaption>
    </section>
  );
};

export default TopContainer;
