import React, { useState, useEffect } from 'react';
import './styles/TopContainer.css';
import Image1 from './styles/Images/image1.jpg';
import Image2 from './styles/Images/image2.jpg';
import Image3 from './styles/Images/image3.jpg';

const imagesAndTexts = [
  { image: Image1, title: 'Title 1', caption: 'Caption 1' },
  { image: Image2, title: 'Title 2', caption: 'Caption 2' },
  { image: Image3, title: 'Title 3', caption: 'Caption 3' },
];

const TopContainer = () => {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex + 1) % imagesAndTexts.length);
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const { image, title, caption } = imagesAndTexts[index];

  return (
    <section className="topContainer">
      <img src={image} alt="Background" className="backGroundImage" />
      <figcaption className="figCaption">
        <h1>{title}</h1>
        <p>{caption}</p>
        <button className="learnMoreButton">Learn More</button>
      </figcaption>
    </section>
  );
};

export default TopContainer;
