import React from 'react';
import './App.css';
import topImage from './Images/WarriorImage.jpg';
import aboutImage from './Images/Customize.jpg';
import netArtImage from './Images/netArt.jpg';
import designImage from './Images/DesignImage.jpg';
import theoryImage from './Images/theoryImage.jpg';
import singUpImage from './Images/SignUp.jpg';

function App() {
  return (
    <main className="main">
      <section className="topContainer">
        <figure>
          <img src={topImage} alt="Painted Image of a Female Warrior" className="backgroundImage" />
          <figcaption className="figCaption">
            {/* news content goes here */}
            <h1>Bringing Fantasy to Life!</h1>
            <p>Our interests lie beyond fantasy but rather within our players.<br></br>
             Join us in our journey where we adventure through the <br></br>
             design and intent behind video games and more.</p>
             <button className="learnMoreButton">Learn More</button>
          </figcaption>
        </figure>
      </section>

      <section className="imageGallery">
    <figure className="galleryItem">
      <img src= {aboutImage} alt="Image 1" className="galleryImage" />
      <figcaption className="galleryCaption">Learn About Us!</figcaption>
    </figure>

    <figure className="galleryItem">
      <img src= {netArtImage} alt="Image 2" className="galleryImage" />
      <figcaption className="galleryCaption">Check out our Web Art!</figcaption>
    </figure>

    <figure className="galleryItem">
      <img src= {designImage} alt="Image 3" className="galleryImage" />
      <figcaption className="galleryCaption">Our Design Page</figcaption>
    </figure>

    <figure className="galleryItem">
      <img src= {theoryImage} alt="Image 3" className="galleryImage" />
      <figcaption className="galleryCaption">Theory</figcaption>
    </figure>

    <img src= {singUpImage} alt="Image 3" className="galleryImage" />
    {/* Add more images as needed */}
  </section>
      {/* Other content of homepage */}
    </main>
  );
}

export default App;
