// Header.js
import React from 'react';
import { Link } from 'react-router-dom';
import './styles/Header.css';
import Logo from './styles/Images/Logo.png'

const Header = () => {
  const [currentSlide, setCurrentSlide] = useState(0); // Assuming initial slide is 0

  // useEffect hook to update slide based on interval
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % figandCaption.length);
    }, 5000);

    return () => clearInterval(interval);
  }, []);
  
  return (
    
    <header className="header">
  
  <nav className="navLinks">
  <Link to="/design">Design</Link>
    <Link to="/theory">Theory</Link>
    <Link to="/about">About</Link>
  </nav>
</header>
  );
};

export default Header;
