import React, { useState, useEffect } from 'react';
import './styles/TopContainer.css';
import BackgroundImage1 from './styles/Images/ACShadows.jpg';
import BackgroundImage2 from './styles/Images/Outlaws.jpg';
import BackgroundImage3 from './styles/Images/FinalShape.jpg';

// 3 rotating images, title and captions
const figandCaption = [
  { image: BackgroundImage1, title: 'Title 1', caption: 'Caption 1' },
  { image: BackgroundImage2, title: 'Title 2', caption: 'Caption 2' },
  { image: BackgroundImage3, title: 'Title 3', caption: 'Caption 3' },
];

const TopContainer = () => {
  const [content, setContent] = useState(figandCaption[0]);

  useEffect(() => {
    const interval = setInterval(() => {
      const currentIndex = figandCaption.findIndex(item => item === content);
      setContent(figandCaption[(currentIndex + 1) % figandCaption.length]);
    }, 5000);

    return () => clearInterval(interval);
  }, [content]);

  const handleDotClick = (index) => {
    setContent(figandCaption[index]);
  };

  return (
    <section className="topContainer">
      <div className="gallery">
        <div className="slide" style={{ backgroundImage: `url(${content.image})` }}>
          <figcaption className="figCaption">
            <h1>{content.title}</h1>
            <p>{content.caption}</p>
            <button className="learnMoreButton">Learn More</button>
          </figcaption>
        </div>
      </div>
      <div className="dots">
        {figandCaption.map((_, i) => (
          <span
            key={i}
            className={`dot ${content === figandCaption[i] ? 'active' : ''}`}
            onClick={() => handleDotClick(i)}
          />
        ))}
      </div>
    </section>
  );
};

export default TopContainer;
