// Header.js
import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './styles/Header.css';
import Logo from './styles/Images/Logo.png';
import slides from './Slides';

const Header = () => {
  const location = useLocation();

  const [currentSlide, setCurrentSlide] = useState(0); // Assuming initial slide is 0

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  return (
    <header className="header">
      <div className="logo">
        <img src={Logo} alt="Logo" />
      </div>
      <nav className="navLinks">
        <Link to="/" className={location.pathname === '/' ? 'active' : ''}>Home</Link>
        <Link to="/design" className={currentSlide === 0 ? 'active' : ''}>Design</Link>
        <Link to="/theory" className={currentSlide === 1 ? 'active' : ''}>Theory</Link>
        <Link to="/about" className={currentSlide === 2 ? 'active' : ''}>About</Link>
      </nav>
    </header>
  );
};

export default Header;
