import React, { useState, useEffect } from 'react';
import './styles/TopContainer.css';
import BackgroundImage1 from './styles/Images/ACShadows.jpg';
import BackgroundImage2 from './styles/Images/Outlaws.jpg';
import BackgroundImage3 from './styles/Images/EldenRingg.png';
import slides from './Slides';

const TopContainer = () => {
  const [content, setContent] = useState(slides[0]);
  const [overlayActive, setOverlayActive] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      const currentIndex = slides.findIndex(item => item === content);
      setContent(slides[(currentIndex + 1) % slides.length]);
    }, 5000);

    return () => clearInterval(interval);
  }, [content]);

  const handleDotClick = (index) => {
    setContent(slides[index]);
  };

  const handleScroll = () => {
    if (window.scrollY > window.innerHeight / 2) {
      setOverlayActive(true);
    } else {
      setOverlayActive(false);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <section className="topContainer">
        <div className="gallery">
          {slides.map((item, i) => (
            <div
              key={i}
              className={`slide ${content === item ? 'active' : ''} ${item.className}`}
            >
              <img src={item.image} alt={item.title} className="backgroundImage" />
              <figcaption className="figCaption">
                <h1>{item.title}</h1>
                <p>{item.caption}</p>
                <button className="learnMoreButton">Learn More</button>
              </figcaption>
            </div>
          ))}
        </div>
        <div className="dots">
          {slides.map((_, i) => (
            <span
              key={i}
              className={`dot ${content === slides[i] ? 'active' : ''}`}
              onClick={() => handleDotClick(i)}
            />
          ))}
        </div>
      </section>
      {/* Additional content to enable scrolling */}
      <div className={`scrollOverlay ${overlayActive ? 'active' : ''}`}>
        <div className="overlayContent">
          {/* Your overlay content goes here */}
          <h2>Overlay Content</h2>
          <p>This is the content that scrolls over the slides.</p>
        </div>
      </div>
      <div className="extraContent">
        <h2>More Content Below</h2>
        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla quis lorem ut libero malesuada feugiat. Nulla porttitor accumsan tincidunt. Curabitur arcu erat, accumsan id imperdiet et, porttitor at sem. Vivamus suscipit tortor eget felis porttitor volutpat. Curabitur aliquet quam id dui posuere blandit.</p>
        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla quis lorem ut libero malesuada feugiat. Nulla porttitor accumsan tincidunt. Curabitur arcu erat, accumsan id imperdiet et, porttitor at sem. Vivamus suscipit tortor eget felis porttitor volutpat. Curabitur aliquet quam id dui posuere blandit.</p>
        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla quis lorem ut libero malesuada feugiat. Nulla porttitor accumsan tincidunt. Curabitur arcu erat, accumsan id imperdiet et, porttitor at sem. Vivamus suscipit tortor eget felis porttitor volutpat. Curabitur aliquet quam id dui posuere blandit.</p>
      </div>
    </>
  );
};

export default TopContainer;
